parameters.inputSchema:
{
  "name": "youtube_strategist_output",
  "description": "Generates three high-performing YouTube title and thumbnail ideas based on title trends, power words, and thumbnail analysis.",
  "type": "object",
  "properties": {
    "title_1": {
      "type": "string",
      "description": "A compelling YouTube video title idea tailored to the channel and based on provided patterns."
    },
    "thumbnail_1": {
      "type": "string",
      "description": "A short visual description of the thumbnail concept that pairs with title_1."
    },
    "title_2": {
      "type": "string",
      "description": "A second unique YouTube video title idea that fits the niche and follows successful patterns."
    },
    "thumbnail_2": {
      "type": "string",
      "description": "A short visual description of the thumbnail concept that pairs with title_2."
    },
    "title_3": {
      "type": "string",
      "description": "A third distinct YouTube video title idea optimized for the channel's audience and niche."
    },
    "thumbnail_3": {
      "type": "string",
      "description": "A short visual description of the thumbnail concept that pairs with title_3."
    }
  },
  "required": ["title_1", "thumbnail_1", "title_2", "thumbnail_2", "title_3", "thumbnail_3"]
}

parameters.text:
=Titles:
{{ $('Aggregate').item.json.Title.join("\n\n") }}

Power Words:
{{ $('Aggregate').item.json['Power Words'].join("\n\n") }}

Thumbnail Analysis:
{{ $('Aggregate').item.json['Thumbnail Analysis'].join("\n\n") }}

The Audience Wants These Things:
{{ $('Comment Analysis').item.json['Do These Things'] }}

parameters.inputSchema:
{
  "name": "youtube_comment_analysis",
  "description": "Analyzes YouTube comments to identify what viewers like, dislike, and are asking for.",
  "type": "object",
  "properties": {
    "doing_well": {
      "type": "array",
      "description": "A list of key things the channel is doing well, based on positive viewer feedback.",
      "items": {
        "type": "string"
      }
    },
    "audience_dislikes": {
      "type": "array",
      "description": "A list of recurring issues or criticisms mentioned by the audience.",
      "items": {
        "type": "string"
      }
    },
    "audience_requests": {
      "type": "array",
      "description": "A list of things viewers are asking for or want to see in future content.",
      "items": {
        "type": "string"
      }
    }
  },
  "required": ["doing_well", "audience_dislikes", "audience_requests"]
}
